const result = {};
const properties_model = $('Edit Fields4').all();

for(const property of properties_model){
  const table = property.json.table;

  if (!result[table]) {
    result[table] = [];
  }

  result[table].push({
    field: property.json.Field,
    type: property.json.Type,
    nullable: property.json.Null,
    key: property.json.Key,
    default: property.json.Default,
    extra: property.json.Extra
  });
};

return result;
